import React, { Component } from 'react';
import { Navigate } from 'react-router-dom';
import FileService from '../services/FileService';

class UploadImageComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            files: null,
            fileUploaded: null,
            uploaderName: ''
        }
    }

    onFileChange = (event) => {
        this.setState({
            files: event.target.files
        });
    }

    onUploaderNameChange = (event) =>{
        this.setState({uploaderName : event.target.value});
    }
    onUpload = (event) => {
        event.preventDefault();
        const formData = new FormData();

        for (const key of Object.keys(this.state.files)) {
            formData.append('files', this.state.files[key]);
        }

        FileService.uploadImage(formData).then((response) => {
            console.log(response.data);
            this.setState({ fileUploaded: true });
        }).catch(error => {
            console.log(error);
        });
    }

    render() {
        if(this.state.fileUploaded){
            return  <Navigate to="/my-images" replace={true} />;
        }

        return (
            <div className='row'>
                <div className='card col-md-6 offset-md-3 mt-5'>
                    <div className='card-body'>
                        <form onSubmit={this.onUpload}>
                            <div>
                                <label>Select a file:</label>
                                <input className='mx-2' id="input-file-upload" type='file' name='file' directory="" webkitdirectory="" onChange={this.onFileChange} multiple></input>
                                <label id="label-file-upload" htmlFor="input-file-upload" className={dragActive ? "drag-active" : "" }>
                                    <div>
                                        <p>Drag and drop your file here or</p>
                                        <button className="upload-button" onClick={onButtonClick}>Upload a file</button>
                                    </div>
                                </label>
                                { dragActive && <div id="drag-file-element" onDragEnter={handleDrag} onDragLeave={handleDrag} onDragOver={handleDrag} onDrop={handleDrop}></div> }
                            </div>
                            <button className='btn btn-success btn-sm mt-3' type='submit' disabled={!this.state.files}>Загрузить</button>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default UploadImageComponent;
